package netz.ClientGUI;

import javafx.application.Application;
import javafx.application.Platform;
import javafx.fxml.FXMLLoader;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.scene.image.Image;
import javafx.stage.Stage;
import netz.Channel;
import netz.ChatClient;
import netz.Client;
import netz.ClientGUI.Controller.ChatController;
import netz.ClientGUI.Controller.ConnectionController;
import netz.ClientGUI.Controller.LoginController;
import netz.Message;
import org.json.JSONException;

import java.io.IOException;
import java.util.Objects;
import java.util.function.Consumer;

public class ChatApplication extends Application {

    private Stage primaryStage;
    private ChatClient client = null;
    ChatController chatController = null;

    public void setPrimaryStage(Stage primaryStage){
        this.primaryStage = primaryStage;
    }

    @Override
    public void start(Stage primaryStage) throws Exception {
        setPrimaryStage(primaryStage);
        // Corner Icon
        Image icon = new Image("data:image/png;base64,");
        // loader | Set the Starting window to be the connection screen
        FXMLLoader loader = new FXMLLoader(getClass().getResource("./resources/fxml/connect.fxml"));
        Parent root = loader.load();
        // Handle Controller logic
        ConnectionController connController = loader.getController();
        connController.setMainApp(this);

        // Set beginning Scene
        Scene scene = new Scene(root);
        this.primaryStage.setScene(scene);
        this.primaryStage.setTitle("Chat App");
        this.primaryStage.setResizable(false);
        this.primaryStage.getIcons().add(icon);
        this.primaryStage.show();
    }

    public void displayErrorMessage(String message){

    }

    public void connectToServer(String pServerIP, int pServerPort){
        try {
            this.client = new ChatClient(pServerIP, pServerPort, this);
            // TODO successfully connected to the Server, switch Scenes to the Login.

        }catch (Exception e){
            // TODO: Add the gui error
            e.printStackTrace();
            this.client = null;
        }
    }

    public void confirmConnection() {
        try {
            changeScene("login", loader -> {
                LoginController logController = loader.getController();
                logController.setMainApp(this);
            });
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public void logIn(String username, String password){
        try {
            this.client.login(username, password);
        }catch (Exception e){
            // TODO: Add some sort of error in gui of what happened
            e.printStackTrace();
        }
    }

    public void confirmLogin() {
        // info: switch the scene
        try {
            changeScene("chat", loader -> {
                Platform.runLater(() -> {
                    try {
                        this.chatController = loader.getController();
                        this.chatController.setMainApp(this);
                        this.initChat();
                    }catch (Exception e) {
                        e.printStackTrace();
                    }
                });
            });
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void initChat() throws JSONException {
        String username = this.client.getNickname();
        String serverIP = this.client.getServerIP();
        int serverPort = this.client.getServerPort();

        this.chatController.initChat(username, serverIP, serverPort);
    }

    public void changeScene(String fxmlFile, Consumer<FXMLLoader> callback) throws IOException {
        Platform.runLater(() -> {
            try {
                FXMLLoader loader = new FXMLLoader(getClass().getResource("./resources/fxml/" + fxmlFile + ".fxml"));
                Parent root = loader.load();
                Scene scene = new Scene(root);

                this.primaryStage.setScene(scene);
                this.primaryStage.show();
                callback.accept(loader);
            } catch (Exception e) {
                e.printStackTrace();
            }
        });

    }

    public void reconnect() {
        try {
            this.client.quit();
            this.client = null;
            this.changeScene("connect", loader -> {
                ConnectionController connectionController = loader.getController();
                connectionController.setMainApp(this);
            });
        } catch(Exception e) {
            e.printStackTrace();
        }
    }

    public void send(String message) {
        //System.out.println("DEBUG: chatapp send: " + message);
        this.client.sendMessage(message);
    }

    public void recieveMessage(int channelID) {
        if (this.chatController != null) {
            this.chatController.receiveMessage(channelID);
        }
    }

    public static void main(String[] args) {
        launch(args);
    }
}
